import React, { Component } from "react";
export default class DocumentTemp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      TempData: [],
      show: false,
      ObjId: "",
    };
  }
  componentDidMount() {
    const { data } = this.props;
    let token = data[0].User.access_token;
    console.log(token);
    fetch(
      "https://api.revvsales.com/api/docstemplate/?page_num=1&status=ACTIVE",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          AccessToken: token,
        },
      }
    )
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          TempData: [data],
        });
      })
      .catch((error) => {
        this.setState({
          error: "Invalid Email or Password",
        });
      });
  }
  handleDoc = (id) => {
    let token = this.props.data[0].User.access_token;
    const l = this.state.TempData[0].Templates.filter((a) => a.id == id);
    console.log(l);
    const data = {
      template_id: id,
    };
    fetch("https://api.revvsales.com/api/docs", {
      method: "POST",
      headers: {
        AccessToken: token,
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) =>
        this.setState({
          objId: data.Document.object_id,
          show: true,
        })
      )
      .then(this.handlePermaLink)
      .catch((error) => console.log(error));
  };
  handlePermaLink = () => {
    let token = this.props.data[0].User.access_token;
    console.log(this.state.objId, "obj");
    const data = {
      object_id: this.state.objId,
      object_type: "DOC",
    };
    fetch(
      "https://api.revvsales.com/api/folders/?page_num=1&sort_by_doc_num=true",
      {
        method: "POST",
        headers: {
          AccessToken: token,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      }
    )
      .then((response) => response.json())
      .then((data) => console.log(data, "link"))
      .catch((error) => console.log(error));
  };
  render() {
    console.log(this.state.TempData);

    return (
      <>
        {this.state.TempData.length > 0 ? (
          <div style={{ display: "flex" }}>
            {this.state.TempData[0].Templates.map((a) => (
              <div style={{ flex: "1px" }}>
                <div>
                  <img
                    src={a.thumbnail}
                    id={a.id}
                    onClick={() => this.handleDoc(a.id)}
                    style={{ height: "95%", width: "200px" }}
                  />
                  <div>{a.title}</div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <></>
        )}
      </>
    );
  }
}
