import React from "react";
import { Component } from "react";
import axios from "axios";
import DocumentTemp from "../Components/DocumentTemp";
export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      data: [],
      error: "",
      org_domain: "ft890220",
    };
  }

  handleChange = (e) => {
    const { name, value } = e.target;

    this.setState({ [name]: value });
  };
  handleLogin = (e) => {
    axios({
      method: "post",
      headers: {
        "Content-Type": "application/json",
        GrantType: "password",
      },
      url: "https://api.revvsales.com/api/v2/auth/initiate-auth",
      data: {
        user_email: "lakshmilucky6651@gmail.com",
        password: "myfamily143",
        org_domain: this.state.org_domain,
      },
    })
      .then((res) => {
        console.log(res);
        if (res.data.User.access_token.length > 0) {
          this.setState({
            data: [res],
          });
        }
      })

      .catch((error) => {
        this.setState({
          error: "Invalid Email or Password",
        });
      });
    //console.log(this.state.data,this.state.data.length)
  };
  handleTemp = () => {
    window.open("https://ft890220.revvsales.com/templates/library");
  };
  render() {
    const { email, password, data } = this.state;
    console.log(data);
    return (
      <>
        {this.state.data.length === 0 ? (
          <div>
            <div>
              <h1 className="logo">
                <img
                  className="logo-icon"
                  src="Revvsales_logo.png"
                  style={{ width: "20%", height: "10%" }}
                />
              </h1>
            </div>
            <div>
              <form>
                <div className="form-group row">
                  <label className="col-sm-2 col-form-label">Email:</label>
                  <div className="col-sm-10">
                    <input
                      type="email"
                      name="email"
                      value={email}
                      onChange={this.handleChange}
                      className="form-control"
                      id="inputEmail3"
                      placeholder="Enter the email"
                    />
                  </div>
                </div>
                <div className="form-group row">
                  <label className="col-sm-2 col-form-label">Password:</label>
                  <div className="col-sm-10">
                    <input
                      type="password"
                      name="password"
                      value={password}
                      onChange={this.handleChange}
                      className="form-control"
                      id="inputPassword3"
                      placeholder="Enter the password"
                    />
                  </div>
                </div>
                <div>
                  <button
                    type="button"
                    onClick={this.handleLogin}
                    className="btn btn-primary mt-5 ml-3"
                  >
                    Login
                  </button>
                </div>
              </form>
              <div style={{ marginTop: "20px", color: "red" }}>
                {this.state.error}
              </div>
            </div>
          </div>
        ) : (
          <>
            <div style={{ padding: "20px" }}>
              <div style={{ display: "flex", marginLeft: "37%" }}>
                <div>
                  <img
                    src={data[0].data.User.image_url}
                    alt={data[0].data.User.first_name}
                  />
                </div>
                <div>
                  <h1>{data[0].data.User.first_name.toUpperCase()}</h1>
                </div>
              </div>
              <div style={{ marginTop: "20px" }}>
                <p>
                  <a
                    class="btn btn-primary"
                    data-toggle="collapse"
                    href="#collapseExample"
                    role="button"
                    aria-expanded="false"
                    aria-controls="collapseExample"
                  >
                    Create Document
                  </a>
                  <button
                    onClick={this.handleTemp}
                    class="btn btn-primary"
                    type="button"
                    data-toggle="collapse"
                    data-target="#collapseExample2"
                    aria-expanded="false"
                    aria-controls="collapseExample2"
                  >
                    Create Template
                  </button>
                </p>
                <div class="collapse" id="collapseExample">
                  <div class="card card-body">
                    <DocumentTemp
                      data={this.state.data}
                      org_domain={this.state.org_domain}
                    />
                  </div>
                </div>
              </div>
            </div>
          </>
        )}
      </>
    );
  }
}
