import React from "react"
import { Component } from "react";
import axios from 'axios'
export default class Login extends Component{
    constructor(props){
        super(props)
            this.state={
                email:"",
                password:""
            }
    }
   
    handleChange = (e) => {
        const { name, value } = e.target;
    
        this.setState({ [name]: value });
      };
          handleLogin = (e) => {
            const data ={
                "user_email": this.state.email,
                "password": this.state.password,
                "org_domain": "ft890220"
            }
            fetch('https://api.revvsales.com/api/v2/auth/initiate-auth', {
          method: 'POST', 
          headers: {
            'Content-Type': 'application/json',
            'GrantType': 'password',
          },
          body: JSON.stringify(data),
        })
        .then(response => response.json())
        .then(data => {
          console.log('Success:', data);
        })
        .catch((error) => {
          console.error('Error:', error);
        });
            }
    render(){
        const {email,password}=this.state
        return(
            <>
            <div>
            <div >
            <h1 class="logo">
            <img class="logo-icon" src="Revvsales_logo.png" style={{width:"20%",height:"10%"}}/>
            </h1>
          </div>
          <div>
            <form>
                <div class="form-group row">
                  <label for="inputEmail3" class="col-sm-2 col-form-label">Email:</label>
                  <div class="col-sm-10">
                    <input type="email" name="email" value={email} onChange={this.handleChange}  class="form-control" id="inputEmail3" placeholder="Enter the email"/>
                  </div>
                </div>
                <div class="form-group row">
                  <label for="inputPassword3" class="col-sm-2 col-form-label">Password:</label>
                  <div class="col-sm-10">
                    <input type="password" name="password" value={password} onChange={this.handleChange} class="form-control" id="inputPassword3" placeholder="Enter the password"/>
                  </div>
                  </div>
                  <div>
                    <button type="button" onClick={this.handleLogin} class="btn btn-primary mt-5 ml-3">Login</button>
                  </div>
                </form>
            </div>
            </div>
            </>
        )
    }
}
