import React from "react"
import { Component } from "react";
import axios from 'axios'
export default class Login extends Component{
    constructor(props){
        super(props)
            this.state={
                email:"",
                password:"",
                status:false,
                data:[],
                error:""
            }
    }
   
    handleChange = (e) => {
        const { name, value } = e.target;
    
        this.setState({ [name]: value });
      };
          handleLogin = (e) => {
            const data ={
                "user_email": this.state.email,
                "password": this.state.password,
                "org_domain": "ft890220"
            }
            fetch('https://api.revvsales.com/api/v2/auth/initiate-auth', {
          method: 'POST', 
          headers: {
            'Content-Type': 'application/json',
            'GrantType': 'password',
          },
          body: JSON.stringify(data),
        })
        .then(response => response.json())
        .then(data => {
            if(data.User.access_token.length>0){
          this.setState({
              data:[data],
            
          }) 
        }
           })
        .catch((error) => {
            
         this.setState({
             error:"Invalid Email or Password"
         })
        });
        console.log(this.state.data,this.state.data.length)
       

            }
    render(){
        const {email,password,status}=this.state
        return(
            <>
            {
                this.state.data.length==0? <div>
                <div >
                <h1 className="logo">
                <img className="logo-icon" src="Revvsales_logo.png" style={{width:"20%",height:"10%"}}/>
                </h1>
              </div>
              <div>
                <form>
                    <div className="form-group row">
                      <label  className="col-sm-2 col-form-label">Email:</label>
                      <div className="col-sm-10">
                        <input type="email" name="email" value={email} onChange={this.handleChange}  className="form-control" id="inputEmail3" placeholder="Enter the email"/>
                      </div>
                    </div>
                    <div className="form-group row">
                      <label  className="col-sm-2 col-form-label">Password:</label>
                      <div className="col-sm-10">
                        <input type="password" name="password" value={password} onChange={this.handleChange} className="form-control" id="inputPassword3" placeholder="Enter the password"/>
                      </div>
                      </div>
                      <div>
                        <button type="button" onClick={this.handleLogin} className="btn btn-primary mt-5 ml-3">Login</button>
                      </div>
                    </form>
            <div style={{marginTop:"20px",color:"red"}}>{this.state.error}</div>
                </div>
            </div>:<>{email}</>
            }
           
            </>
        )
    }
}
