import React, { Component } from "react";
export default class DocumentTemp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      TempData: [],
    };
  }
  componentDidMount() {
    const { data } = this.props;
    let token = data[0].User.access_token;
    console.log(token);
    fetch(
      "https://api.revvsales.com/api/docstemplate/?page_num=1&status=ACTIVE",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          AccessToken: token,
        },
      }
    )
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          TempData: [data],
        });
      })
      .catch((error) => {
        this.setState({
          error: "Invalid Email or Password",
        });
      });
  }
  handleDoc = () => {
    const data = {
      object_id: "DOC-ZXrgxu0SEvML",
      object_type: "DOC",
    };
    fetch("https://api.revvsales.com/api/v2/auth/initiate-auth", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.User.access_token.length > 0) {
          this.setState({
            data: [data],
          });
        }
      })
      .catch((error) => {
        this.setState({
          error: "Invalid Email or Password",
        });
      });
    //console.log(this.state.data,this.state.data.length)
  };
  render() {
    console.log(this.state.TempData);

    return (
      <>
        {this.state.TempData.length > 0 ? (
          <div style={{ display: "flex" }}>
            {this.state.TempData[0].Templates.map((a) => (
              <div style={{ flex: "1px" }}>
                <div>
                  <img
                    src={a.thumbnail}
                    onClick={this.handleDoc}
                    style={{ height: "95%", width: "200px" }}
                  />
                  <div>{a.title}</div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <></>
        )}
      </>
    );
  }
}
